# Cell
import numpy as np

# Cell
def fullfactorial2k(k: int) -> np.ndarray:
    """
    Creates a full factorial design with 2 levels per factor
    in standard order

    k: number of factors

    Return
    """
    n = 2**k
    matrix = np.zeros((n,k))
    for j in range(k):
        level = 1
        for i in range(n):
            repeats = 2**((j+1)-1)
            if i % repeats == 0:
                level *= -1
            matrix[i,j] = level
    return matrix
